#include <bits/stdc++.h>using namespace std; int main() {    int t;    cin >> t;    while (t--) {        int n;        cin >> n;        vector<int> a(n);        for (int i = 0; i < n; i++) {            cin >> a[i];        }        // Just count number of 0 if found consecutively        // otherwise make the count 0 and update the max blank with local longest blank        int max_blank_length {INT_MIN};        int cnt {0};        for (int i = 0; i < n; i++) {            if (a[i] == 0) {                cnt ++;            }            else if (a[i] == 1) {                max_blank_length = max(max_blank_length, cnt);                cnt = 0;            }            if (i == n - 1) {                max_blank_length = max(max_blank_length, cnt);            }        }        cout << max_blank_length << endl;    }    return 0;}