#include <bits/stdc++.h>using namespace std;Â int main() {    int t;    cin >> t;    while (t--) {        int n;        cin >> n;        vector<int> a(n);        for (int i = 0; i < n; i++) {            cin >> a[i];        }        int cnt_1 {0};        int cnt_minus1 {0};        for (int i = 0; i < n; i++) {            if (a[i] == 1) {                cnt_1++;            }             else if (a[i] == -1) {                cnt_minus1++;            }        }        int minOps {0};        while (cnt_1 < cnt_minus1 || cnt_minus1 % 2 != 0) {            cnt_1 ++;            cnt_minus1 --;            minOps++;        }        cout << minOps << "\n";    }    return 0;}