#include <bits/stdc++.h>using namespace std;Â int main() {    int t;    cin >> t;    while (t--) {        int n, k, x;        cin >> n >> k >> x;        if (x == 1) {            if (k == 1 || (k == 2 && n % 2 == 1)) {                cout << "NO" << endl;            }            else if (n % 2 == 0) {                cout << "YES\n";                cout << n / 2 << endl;                for (int i = 2; i <= n; i+=2) {                    cout << 2 << " ";                }            }            else if (n % 3 == 0) {                cout << "YES\n";                cout << n / 3 << endl;                for (int i = 3; i <= n; i+=3) {                    cout << 3 << " ";                }            }            else {                cout << "YES\n";                cout << ((n / 2) - 1) + 1 << endl; // one 3 and rest 2s (n/2 - 1)                cout << 3 << " ";                for (int i = 2; i <= n - 3; i+=2) {                    cout << 2 << " ";                }            }        }        else {            cout << "YES\n";            cout << n << endl;            for (int i = 1; i <= n; i++) {                cout << 1 << " ";            }        }    }    return 0;}