#include <bits/stdc++.h>using namespace std; int main() {    int n;    cin >> n;    vector<int> a(n);    int min_val_from_zero {INT_MAX};    for (int i = 0; i < n; i++) {        cin >> a[i];        int distance_from_zero = abs(a[i] - 0);        min_val_from_zero = min(min_val_from_zero, distance_from_zero);    }    // optimised approach    cout << min_val_from_zero << endl;}