#include <bits/stdc++.h>using namespace std;Â int main() {    int t;    cin >> t;    while (t--) {        vector<vector<char>> grid;        for (int i = 0; i < 10; i++) {            vector<char> a(10);            for (int j = 0; j < 10; j++) {                cin >> a[j];            }            grid.push_back(a);        }        // logic comes from patterns in i and j in matrix formed        int value = 0;        for (int i = 0; i < 10; i ++) {            for (int j = 0; j < 10; j++) {                if (grid[i][j] == 'X' && (i==0 || j==0 || i==9 || j==9)) {                    value+=1;                }                else if (grid[i][j] == 'X' && (i==1 || j==1 || i==8 || j==8)) {                    value+=2;                }                else if (grid[i][j] == 'X' && (i==2 || j==2 || i==7 || j==7)) {                    value+=3;                }                else if (grid[i][j] == 'X' && (i==3 || j==3 || i==6 || j==6)) {                    value+=4;                }                else if (grid[i][j] == 'X' && (i==4 || j==4 || i==5 || j==5)) {                    value+=5;                }            }        }        cout << value << endl;    }    return 0;}