#include <bits/stdc++.h>using namespace std; int main() {    int t;    cin >> t;    while (t--) {        int n;        cin >> n;        vector<int> a(n);        unordered_map<int, int> freq;        for (int i = 0; i < n; i++) {            cin >> a[i];            freq[a[i]] ++;        }        if (freq.size() == 1) {            cout << "Yes\n";        }        else if (freq.size() == 2) {            if (n%2 == 0 && freq[a[0]] == n/2) {                cout << "Yes\n";            }            else if (n%2 == 1 && (freq[a[0]] == (n+1)/2 || freq[a[0]] == (n-1)/2)){                cout << "Yes\n";            }            else {                cout << "No\n";            }        }        else {            cout << "No\n";        }    }    return 0;}