#include <bits/stdc++.h>using namespace std;Â int main() {    int t;    cin >> t;    while (t--) {        int n, x;        cin >> n >> x;        vector<int> a(n);        for (int i = 0; i < n; i++) {            cin >> a[i];        }                vector<int> distances;        distances.push_back(0);        for (int i = 0; i < n; i++) {            distances.push_back(a[i]);        }        distances.push_back(x);                // Finding max distance is the min fuel requirement        int maxDist {INT_MIN};        for (int i = 1; i < distances.size(); i++) {            int distance = distances[i] - distances[i-1];            if (i == distances.size() - 1) {                distance = distance*2;            }            maxDist = max(maxDist, distance);        }        cout << maxDist << "\n";    }    return 0;}