#include<iostream>#include<vector> using namespace std; int main() {    int t;    cin >> t;    while (t--) {        int n, k;        cin >> n >> k;        vector <int> nums(n);        bool sorted = true;        for (int i = 0; i < n; i++){            cin >> nums[i];            if (i >= 1 && nums[i] < nums[i-1]) {                sorted = false;            }        }        if (sorted || k >= 2) {            cout << "YES\n";        }        else {            cout << "NO\n";        }    }    return 0;}