#include <bits/stdc++.h>using namespace std;Â int main() {    int t;    cin >> t;    while (t--) {        int n;        cin >> n;        vector<int> a(n);        for (int i = 0; i < n; i++) {            cin >> a[i];        }        sort(a.begin(), a.end());        int best_mex {0};        int curr_mex {0};        for (int i = 1; i < n; i++) {            if (a[i] == a[i-1] + 1) {                curr_mex++;            } else if (a[i] != a[i-1]) {                curr_mex = 0;            }            best_mex = max(best_mex, curr_mex);        }        cout << best_mex + 1 << "\n";    }    return 0;}